// +build js, wasm

package ordervalidator

import (
	"math/big"
	"testing"
	"time"

	"github.com/0xProject/0x-mesh/constants"
	"github.com/0xProject/0x-mesh/ethereum"
	"github.com/0xProject/0x-mesh/ethereum/ethrpcclient"
	"github.com/0xProject/0x-mesh/ethereum/ratelimit"
	"github.com/0xProject/0x-mesh/zeroex"
	"github.com/ethereum/go-ethereum/common"
	ethrpc "github.com/ethereum/go-ethereum/rpc"
)

const (
	defaultEthRPCTimeout = 5 * time.Second
)

var (
	signedOrders   []*zeroex.SignedOrder
	orderValidator *OrderValidator
)

func init() {
	rpcClient, err := ethrpc.Dial(constants.GanacheEndpoint)
	if err != nil {
		panic(err)
	}
	ethRPCClient, err := ethrpcclient.New(rpcClient, defaultEthRPCTimeout, ratelimit.NewUnlimited())
	if err != nil {
		panic(err)
	}
	orderValidator, err = New(ethRPCClient, constants.TestChainID, constants.TestMaxContentLength, ethereum.GanacheAddresses)
	if err != nil {
		panic(err)
	}

	multiAssetAssetData := common.Hex2Bytes("")
	order := zeroex.Order{
		ChainID:               big.NewInt(constants.TestChainID),
		MakerAddress:          constants.GanacheAccount1,
		TakerAddress:          constants.NullAddress,
		SenderAddress:         constants.NullAddress,
		FeeRecipientAddress:   constants.NullAddress,
		MakerAssetData:        multiAssetAssetData,
		MakerFeeAssetData:     multiAssetAssetData,
		TakerAssetData:        multiAssetAssetData,
		TakerFeeAssetData:     multiAssetAssetData,
		Salt:                  big.NewInt(int64(time.Now().Nanosecond())),
		MakerFee:              big.NewInt(0),
		TakerFee:              big.NewInt(0),
		MakerAssetAmount:      big.NewInt(100),
		TakerAssetAmount:      big.NewInt(42),
		ExpirationTimeSeconds: big.NewInt(time.Now().Add(24 * time.Hour).Unix()),
		ExchangeAddress:       ethereum.GanacheAddresses.Exchange,
	}
	signedOrder, err := zeroex.SignTestOrder(&order)
	if err != nil {
		panic(err)
	}
	for i := 1; i < 100; i++ {
		signedOrders = append(signedOrders, signedOrder)
	}
}

func BenchmarkComputeOptimalChunkSizes(b *testing.B) {
	for n := 0; n < b.N; n++ {
		orderValidator.computeOptimalChunkSizes(signedOrders)
	}
}
